pma:database.pma:table:
CREATE TABLE `manager` (
  `emp_id` int(11) NOT NULL,
  PRIMARY KEY (`emp_id`),
  CONSTRAINT `manager_ibfk_1` FOREIGN KEY (`emp_id`) REFERENCES `employee` (`emp_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `seat` (
  `seat_no` varchar(5) NOT NULL,
  `seat_status` varchar(15) DEFAULT NULL,
  `date_time` datetime NOT NULL DEFAULT '2023-01-16 10:30:00',
  PRIMARY KEY (`seat_no`,`date_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE TABLE `staff` (
  `emp_id` int(11) NOT NULL DEFAULT 100,
  PRIMARY KEY (`emp_id`),
  CONSTRAINT `staff_ibfk_1` FOREIGN KEY (`emp_id`) REFERENCES `employee` (`emp_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
